import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import UserAnalytics from "./UserAnalytics";
import Notifications from "./Notifications";
import ActivityLog from "./ActivityLog";
import UserGrowthChart from "./UserGrowthChart";
import UserActivityChart from "./UserActivityChart";
import UserManagement from "./UserManagement";

const AdminDashboard = () => {
    const [users, setUsers] = useState([]);
    const [user, setUser] = useState(null);
    const [loading, setLoading] = useState(true);
    const [darkMode, setDarkMode] = useState(() => {
        return localStorage.getItem("darkMode") === "true"; // ✅ Persist Dark Mode
    });

    const token = localStorage.getItem("token");
    const navigate = useNavigate();

    // ✅ Ensure User Authentication
    useEffect(() => {
        if (!token) {
            navigate("/login");
        } else {
            fetchUsers();
        }
    }, [token, navigate]);

    // ✅ Fetch Users & Logged-in User Data
    const fetchUsers = async () => {
        try {
            const response = await fetch("http://localhost:5050/users", {
                method: "GET",
                headers: { Authorization: `Bearer ${token}` },
            });

            if (!response.ok) {
                throw new Error("Failed to fetch users");
            }

            const data = await response.json();
            setUsers(Array.isArray(data) ? data : []);

            // Load User Info from LocalStorage
            setUser({
                id: localStorage.getItem("userId"),
                role: localStorage.getItem("role"),
            });

            setLoading(false);
        } catch (error) {
            console.error("Error fetching users:", error);
            setLoading(false);
        }
    };

    // ✅ Logout Function
    const handleLogout = () => {
        localStorage.clear();
        navigate("/login");
        window.location.reload(); // ✅ Ensures clean logout
    };

    // ✅ Toggle Dark Mode
    const toggleDarkMode = () => {
        const newMode = !darkMode;
        setDarkMode(newMode);
        localStorage.setItem("darkMode", newMode);
    };

    // ✅ Styles
    const styles = {
        container: {
            padding: "20px",
            fontFamily: "Arial, sans-serif",
            background: darkMode ? "#222" : "#f5f5f5",
            color: darkMode ? "white" : "black",
            minHeight: "100vh",
        },
        header: {
            display: "flex",
            justifyContent: "space-between",
            alignItems: "center",
            marginBottom: "15px",
        },
        grid: {
            display: "grid",
            gridTemplateColumns: "repeat(auto-fit, minmax(300px, 1fr))",
            gap: "20px",
            marginBottom: "20px",
        },
        table: {
            width: "100%",
            borderCollapse: "collapse",
            marginTop: "20px",
            background: darkMode ? "#333" : "white",
            color: darkMode ? "white" : "black",
        },
        th: {
            background: "#007bff",
            color: "white",
            padding: "10px",
            textAlign: "left",
        },
        td: {
            padding: "10px",
            borderBottom: darkMode ? "1px solid #555" : "1px solid #ddd",
        },
        button: {
            padding: "10px 15px",
            background: "#007bff",
            color: "white",
            border: "none",
            cursor: "pointer",
            marginLeft: "10px",
            borderRadius: "5px",
        },
        logoutButton: {
            background: "red",
        },
    };

    // ✅ Display Loading until User Data is Loaded
    if (loading) return <p>Loading dashboard...</p>;

    return (
        <div style={styles.container}>
            {/* Header Section */}
            <div style={styles.header}>
                <h1>Admin Dashboard</h1>
                <div>
                    {user && <span>👤 {user.role.toUpperCase()} (ID: {user.id})</span>}
                    <button style={styles.button} onClick={toggleDarkMode}>
                        {darkMode ? "☀️ Light Mode" : "🌙 Dark Mode"}
                    </button>
                    <button style={{ ...styles.button, ...styles.logoutButton }} onClick={handleLogout}>
                        🚪 Logout
                    </button>
                </div>
            </div>

            {/* Grid Layout for Widgets */}
            <div style={styles.grid}>
                <UserAnalytics darkMode={darkMode} />
                <Notifications darkMode={darkMode} />
                <UserGrowthChart darkMode={darkMode} />
                <UserActivityChart darkMode={darkMode} />
            </div>

            {/* Activity Log & User Management */}
            <ActivityLog darkMode={darkMode} />
            <UserManagement darkMode={darkMode} />

            {/* User Table */}
            <h2>User List</h2>
            <table style={styles.table}>
                <thead>
                    <tr>
                        <th style={styles.th}>ID</th>
                        <th style={styles.th}>Username</th>
                        <th style={styles.th}>Email</th>
                        <th style={styles.th}>Role</th>
                    </tr>
                </thead>
                <tbody>
                    {users.map(user => (
                        <tr key={user.id}>
                            <td style={styles.td}>{user.id}</td>
                            <td style={styles.td}>{user.username}</td>
                            <td style={styles.td}>{user.email}</td>
                            <td style={styles.td}>{user.role}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default AdminDashboard;
